import React from 'react';

const Input = () => {
    [data,setData] = useState('')
    const handleClick = () =>{
        updateBoards(data)
        setData('')
    }
    return (
        <div className='boards__input'>
            <input value = {data}  type="text" name="" id="" onChange = {e=>setData(e.target.value)}/>
            <button onClick={() => handleClick()}>
                Добавить задачку
            </button>
        </div>
    );
};

export default Input;
